mapper.select:
	SELECT * 
	  FROM order_view
	 WHERE id=#{id}
	   AND result LIKE '%'||#{result}||'%'
       AND oseq=#{oseq}

mapper.update:
UPDATE order_detail
   SET result='2'
 WHERE odseq=#{odseq}

mapper.select:

		SELECT *
			FROM
			  (SELECT rownum rn, oseq, odseq, pseq, quantity, pname, id, name, discount, totalprice, zip_num, address
             ,phone, price2, image, memo,indate, result
			     FROM order_view
			    WHERE id LIKE '%'||#{key}||'%'
			    AND rownum <= #{criteria.pageNum} * #{criteria.numPerPage}
			    ORDER BY odseq
			   )
			WHERE rn > (#{criteria.pageNum}-1) * #{criteria.numPerPage}
